css:
@import url("/en/assets/css/block-shimmer.scss");

@media(max-width: 768px) {
    .btnCustom{
        min-width: 200px;
    }
    .buttonCustom{
            position: relative;
            margin-bottom: 2rem;
            margin-top: 0rem !important;
    }   
    /deep/ .autoCompleteGoogle form .input-group app-jw-google-autocomplete input{
            width: 170px ;
            text-overflow: ellipsis ;
            white-space: nowrap ;
            max-width: 200px ;
            padding-left: 0rem !important;
    }
    .homepage-header {
        padding: 8px 16px;
    }
}

.homepage-banner {
    display: grid;
    grid-template-columns: 3fr 6fr 3fr;
    gap: 20px;
    padding: 3rem;
    // background: black;
    // background: linear-gradient(0deg, #000 0%, rgba(0, 0, 0, 0.00) 100%), var(--header_bg_color);
    background: linear-gradient(0deg, #000 -22%, rgba(0, 0, 0, 0.00) 95%), var(--header_bg_color);
    margin-top: 80px;
}
.headerColorStatic{
    width: 100%;
    height: 80px;
    background-color: var(--header_bg_color);
    top: 80px;
    position: fixed;
    z-index: 10;
    padding-top: 12px;
    transition: height 2s;
}
.homepage-banner .main-banner {
    text-align: center;
    display: flex;
    justify-content: center;
    flex-direction: column;
}

.homepage-banner .banner-heading {
    font-family: 'Poppins', sans-serif !important;
    font-style: normal;
    font-weight: 600;
    font-size: 38px;
    line-height: 48px;
    color: #ffffff;
    margin: 0;
    margin-bottom: 49px;
}

// .homepage-banner .banner-heading>span {
//     color: var(--theme);
// }

.homepage-banner .main-banner p {
    font-family: 'Poppins', sans-serif !important;
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 22px;
    color: #ffffff;
    margin-bottom: 20px;
}

.main-banner .main-banner-form,
.main-banner form {
    text-align: -webkit-center;
}

.main-banner form .form {
    max-width: 600px;

}
.setPadd{
 padding: 0px 2rem;
}
.main-banner .elementor-widget-container {
    max-width: 600px;
    background: white;
    padding: 4px 4px 4px 36px;
    border-radius: 30px;
    position: relative;
}

.main-banner .input-group {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    border: 0;
    justify-content: space-between;
}

.main-banner .location-icon {
    position: absolute;
    left: 29px;
    top: 18px;
}

.main-banner input {
    border-color: transparent !important;
    padding-top: 20px;
    padding-bottom: 20px;
    width: calc(100% - 203px);
    background: 0 0;
    padding-left: 15px;
    padding-right: 0px;
    border: 0;
    border-bottom: 1px solid;
    font-size: small;
    font-weight: 400;
}

.main-banner button {
    background-color: var(--theme);
    color: #fff;
    border-radius: 30px;
    font-weight: 400;
    font-size: 16px;
    padding: 12px 40px;
    border: 0;
}
.main-banner button:hover{
    border: none;
    color: white;
    text-align: center;
    font-size: 16px;
    // opacity: 1;
    transition: 0.3s;
    opacity: 0.6
}
.homepage-banner .image-banner {
    display: flex;
    align-items: flex-end;
    justify-content: center;
}

.homepage-banner .image-banner img {
    width: 100%;
}

.homepage-banner .main-banner .main-banner-mobile-decoration {
    display: none;
}

.location {
    position: relative;
}

.location .btnView {
    position: absolute;
    right: 5px;
    top: 5px;
}

.main-banner .btn {
    border-radius: 30px !important;
}

.homepage-banner .main-banner .main-banner-image {
    display: none;
    width: 100%;
    margin-bottom: 20px;
}

@media(max-width: 768px) {
    :host /deep/ .header-icon{
        color: white !important;
    }
    :host /deep/ .fillColor{
         color: white !important;
    }
    .homepage-banner{
        margin-top: 0px;
    }
    .homepage-banner {
        padding: 10rem 0px;
        display: block;
        background: linear-gradient(0deg, #000 -19.82%, rgba(0, 0, 0, 0.00) 76.93%), var(--header_bg_color) !important;
    }

    .homepage-banner .image-banner {
        display: none;
    }

    .homepage-banner .main-banner {
           display: block;
               padding-top: 73px;
    }

    .homepage-banner .main-banner .main-banner-mobile-decoration {
        display: block;
        width: 40%;
        position: absolute;
        right: -16px;
        top: 50px;
        transform: rotate(-55.14deg);
    }

    .homepage-banner .main-banner .main-banner-image {
        display: block;
    }

    .homepage-banner .main-banner .banner-heading {
        width: 100%;
        font-family: 'Poppins', sans-serif !important;
        font-style: normal;
        font-weight: 600;
        font-size: 26px;
        line-height: 140%;
        margin-bottom: 32px;
    }

    .homepage-banner .main-banner p {
        text-align: center;
        margin-bottom: 12px;
    }

    .homepage-banner .main-banner form {
        padding: 0px 20px 18px;
    }

    .main-banner .location-icon {
        top: 13px;
    }

    .main-banner input {
        height: 40px !important;
    }

    .main-banner button {
        padding: 12px 20px;
        font-size: 11px;
    }
    .main-banner button:hover{
        border: none;
        color: white;
        text-align: center;
        font-size: 11px;
        // opacity: 1;
        transition: 0.3s;
        opacity: 0.6
    }
}

.store-abstract {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    margin-bottom: 40px;
}

.store-abstract .heading {
    font-size: 36px;
    text-align: center;
    font-style: italic;
    background: gold;
    color: black;
    margin: 0;
    padding: 20px 0px;
}

.store-abstract .online-order-cover {
    background: url("https://d2sz1kgdtrlf1n.cloudfront.net/task_images/SmYr1684481116308-onlineorder.jpeg");
    background-size: cover;
}

.newPage.fetchlocation {
    position: relative;
    display: flex;
    justify-content: center;
}

.fetch-location {
    position: fixed !important;
    top: 0;
    left: 0;
    width: 100%;
}

@media(max-width: 768px) {
    .store-abstract {
        grid-template-columns: 1fr;
    }

    .store-abstract .heading {
        font-size: 24px;
    }
}

.fetchlocation app-header-dynamic .top-header {
    height: 7rem !important;
    background-color: transparent !important;
    border-bottom: 2px solid transparent !important;
    position: absolute;
}

.videoStandBy {
    position: absolute;
    top: 0px;
    left: 0px;
    bottom: 0px;
    right: 0;
    background-size: cover;
}

.video-wrapper {
    position: absolute;
    height: 100%;
    width: 100%;
    overflow: hidden;

}

.video {
    width: 100%;
    height: 100%;
    transform: scale(1.23);
    animation-name: IncOpacity;
    animation-duration: 2.8s
}

.video2 {
    width: 100%;
    top: 0px;
    object-fit: cover;
    object-position: center;
    left: 0px;
    bottom: 0px;
    margin: auto;
    height: 100%;
}

.overlay {
    background-image: linear-gradient(0deg, rgba(38, 37, 37, 0.5), transparent);
}

.anim {
    height: 12px;
    width: 12px;
    position: absolute;
    background-color: var(--theme);
    opacity: 0;
    border-radius: 50%;
    animation-name: expandRing;
    animation-duration: 1.5s;
    animation-delay: .75s;
}

.anim2 {
    animation-delay: 1s !important;
}

@keyframes expandRing {
    0% {
        opacity: 0;
        tranform: scale(1);
    }

    50% {
        opacity: 0.25;
        tranform: scale(20);
    }

    100% {
        transform: scale(50);
        opacity: 0
    }
}

@-webkit-keyframes expandRing {
    0% {
        opacity: 0;
        tranform: scale(1);
    }

    50% {
        opacity: 0.25;
        tranform: scale(30);
    }

    100% {
        transform: scale(60);
        opacity: 0
    }
}

@-moz-keyframes expandRing {
    0% {
        opacity: 0;
        tranform: scale(1);
    }

    50% {
        opacity: 0.25;
        tranform: scale(30);
    }

    100% {
        transform: scale(60);
        opacity: 0
    }
}

@keyframes IncOpacity {
    0% {
        opacity: 0;
    }

    98% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}

@-webkit-keyframes expandRing {
    0% {
        opacity: 0;
    }

    98% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}

@-moz-keyframes expandRing {
    0% {
        opacity: 0;
    }

    98% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}

.newPage /deep/ app-header-dynamic .top-header .header .nav-left .merchant-logo .logo img {
    display: none;
}

.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .profile i {
    color: var(--white) !important;
}

.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .dropdown-menu {
    top: 34px;
    min-width: 0px;
    border-top-right-radius: 10px !important;
    border-bottom-left-radius: 10px !important;
    margin-top: 10px;

}

.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .changeUrl,
.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .notification,
.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .search {
    display: none;
}

.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .language a {
    padding: 5px 10px !important;
}

.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items li:not(.drop) a:not(.dropAnchor) {
    background-color: var(--theme);
    color: white;
    border: 1px solid var(--theme);
    border-top-right-radius: 10px !important;
    border-bottom-left-radius: 10px !important;
    height: 35px;
    margin-right: 10px;
    transition: 0.5s;
}

.newPage /deep/ app-header-dynamic .top-header .header .nav-right .items li:not(.drop) a:not(.dropAnchor):hover {
    opacity: 1 !important;
    color: var(--theme) !important;
    background-color: white;
    border: 1px solid var(--theme);
}

.newPage .fetch-location {
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
    height: auto;
    position: relative;


}

.newPage .fetch-location::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 1;
    background-image: inear-gradient(0deg, rgba(38, 37, 37, 0.4), rgba(38, 37, 37, 0));
    ;
    opacity: 0.6;
}

.newPage .fetch-location .languageDrop {
    position: absolute;
    top: 25px;
    left: 25px;
    z-index: 10;
}

.newPage .fetch-location .login {
    position: absolute;
    top: 25px;
    right: 25px;
    z-index: 10;
}

.newPage .fetch-location .login .loginBtn {
    font-family: 'Poppins', sans-serif;
    font-size: 16px;
    color: var(--white);
    background-color: var(--theme);
    border: 1px solid var(--theme);
    transition: 0.5s;
    max-width: 150px;
    min-width: 100px;
    height: 35px;
}

.newPage .fetch-location .login .loginBtn:hover {
    color: var(--theme);
    background-color: var(--white);
    border: 1px solid var(--theme);
}

.newPage .fetch-location .main {
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    margin: auto;
    height: 100%;
    width: 100%;
    display: flex;
}

.newPage .fetch-location .main .root {
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: center;
    margin: auto;
    max-width: 1040px;
}

.newPage .fetch-location .main .root .pop {
    z-index: 10;
    min-width: 538px;
    padding: 25px 80px;
    width: auto;
    border-radius: 50px;

}

.newPage .fetch-location .main .root .pop .logo {
    height: 50px;
    text-align: center;

    animation-name: MoveUp;
    animation-duration: 1.5s;
}

@keyframes MoveUp {
    0% {
        transform: translateY(50px);
        opacity: 0;
    }

    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}

@-webkit-keyframes MoveUp {
    0% {
        transform: translateY(50px);
        opacity: 0;
    }

    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}

@-moz-keyframes MoveUp {
    0% {
        transform: translateY(50px);
        opacity: 0;
    }

    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}

.newPage .fetch-location .main .root .pop .logo img {
    max-width: 100%;
    max-height: 100%;
    height: auto;
    width: auto;
}

.newPage .fetch-location .main .root .pop .heading h1 {
    text-align: center;
    font-family: 'Poppins', sans-serif;
    font-size: 24px;
    color: var(--white);
    margin: 40px 0 30px;
    animation-name: MoveUp;
    animation-duration: 1.5s;
}

.newPage .fetch-location .main .root .pop .form {
    position: relative;
    min-height: 150px;
    height: 100%;
    width: 100%;
    -webkit-transition: all 600ms;
    transition: all 600ms;
    z-index: 20;
}

.newPage .fetch-location .main .root .pop .displayCustom {
    display: block;
}

.newPage .fetch-location .main .root .pop .hideCustom {
    display: none;
}

.newPage .fetch-location .main .root .pop .viewContent {
    position: absolute;
    width: 100%;
}

.newPage .fetch-location .main .root .pop .viewContent .location {
    margin-bottom: 20px;
}

.newPage .fetch-location .main .root .pop .viewContent .btnView button {
    min-width: 50%;
    height: 50px;
    color: var(--white);
    border: 1px solid var(--theme);
    background-color: var(--theme);
    border-top-right-radius: 25px !important;
    border-bottom-left-radius: 25px !important;
    font-size: 16px;
    font-style: normal;
    font-stretch: normal;
    line-height: normal;
    letter-spacing: normal;
    font-family: 'Poppins', sans-serif;
    margin-bottom: 20px;
    transition: 0.3s all;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
}

.newPage .fetch-location .main .root .pop .viewContent .btnView button:hover {
    color: var(--theme);
    border: 1px solid var(--theme);
    background-color: var(--white);
    background-image: None;
}

.newPage .fetch-location .main .root .pop /deep/ .autoCompleteGoogle form .input-group app-jw-google-autocomplete {
    height: 50px !important;
    width: 100% !important;
    background: None !important;
}

form .form .location /deep/ .autoCompleteGoogle form .input-group app-jw-google-autocomplete {
    border-top-left-radius: 30px;
    border-bottom-left-radius: 30px;
}

form .form .location /deep/ .autoCompleteGoogle .autoCompleteGoogleForm {
    background: white;
    border-radius: 30px;
}

form .form .location /deep/ .autoCompleteGoogle .autoCompleteGoogleForm>div:not(.errorpop):not(.infopop) {
    justify-content: space-between;
}

form .form .location /deep/ .autoCompleteGoogle .autoCompleteGoogleForm .auto-detect {
    border-radius: 30px;
    display: none;
}

form .form .location /deep/ .autoCompleteGoogle form .input-group app-jw-google-autocomplete {
    height: 48px;
}

@media only screen and (min-width: 768px) {
    form .form .location /deep/ .autoCompleteGoogle form .input-group app-jw-google-autocomplete {
        height: 56px;
        background-color: white ;
    }
}

.newPage .fetch-location .main .root .pop /deep/ .autoCompleteGoogle .auto-detect {
    position: absolute;
    right: 0;
    height: 50px !important;
    width: 40px !important;
    background: None !important;
}

.newPage .fetch-location .main .root .pop /deep/ .autoCompleteGoogle .auto-detect .hideSpan {
    display: none !important;
}

.newPage .fetch-location .main .root .pop /deep/ .autoCompleteGoogle form .input-group app-jw-google-autocomplete input {
    background: #fff !important;
    border: 1px solid #e5e5e5 !important;
    border-radius: 3px !important;
    border-top-right-radius: 25px !important;
    border-bottom-left-radius: 25px !important;
    font-family: 'Poppins', sans-serif;
    font-size: 16px;
    font-weight: normal;
    font-style: normal;
    font-stretch: normal;
    line-height: normal;
    letter-spacing: normal;
    color: #343434 !important;
    padding-left: 50px;
    padding-right: 50px;
}
/deep/ .pac-container{
        border-radius: 1.5rem !important;
}
/deep/ .autocomplete-adress{
        top: 6rem;
}
/deep/ #mapPopupAddress{
    height: 48px !important;
    border-radius: 1rem !important;
    margin: 8px !important;
    width: 90% !important;
}
/deep/ #mapPopupAddress .autocomplete-adress{
    top: 3rem !important
}
.newPage .fetch-location .main .root .pop .back-link {
    font-family: 'Poppins', sans-serif;
    font-size: 15px;
    color: #9b9b9b;
    transition: 0.5s;
}

.newPage .fetch-location .main .root .pop .back-link span {
    cursor: pointer;
}

.newPage .fetch-location .main .root .pop .back-link span:hover {
    color: black;
}

.newPage .fetch-location .main .root .pop .hideVisibility {
    visibility: hidden;
    height: 0;
}

.newPage .fetch-location .main .root .pop .showVisibility {
    visibility: visible;
    height: auto;
    text-align: center;
    animation-name: MoveUp;
    animation-duration: 1.5s;
}

.newPage .fetch-location .main .root .pop .loginAccount {
    margin-top: 50px;
}

.newPage .fetch-location .main .root .pop .loginAccount p {
    font-family: 'Poppins', sans-serif;
    font-size: 16px;
    font-weight: normal;
    font-style: normal;
    font-stretch: normal;
    line-height: normal;
    letter-spacing: normal;
    text-align: center;
}

.newPage .fetch-location .main .root .pop .loginAccount p .already {
    color: white;
    opacity: 0.7;
}

.newPage .fetch-location .main .root .pop .loginAccount p span.loginText {
    -webkit-transform: perspective(1px) translateZ(0);
    transform: perspective(1px) translateZ(0);
    display: inline-block;
    cursor: pointer;
    color: #2396ff;
}

.newPage .fetch-location .main .root .pop .loginAccount p span.loginText:before {
    content: "";
    position: absolute;
    z-index: -1;
    left: 0;
    right: 100%;
    bottom: 0;
    background: #2396ff;
    height: 4px;
    -webkit-transition-property: right;
    transition-property: right;
    -webkit-transition-duration: 0.3s;
    transition-duration: 0.3s;
    -webkit-transition-timing-function: ease-out;
    transition-timing-function: ease-out;
}

.newPage .fetch-location .main .root .pop .loginAccount p span.loginText:hover:before {
    right: 0;
}

.newPage .product-shimmer-container {
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    margin: auto;
    max-width: 500px;
    height: fit-content;
    width: 100%;
    z-index: 100;
    padding: 15px;
    background-color: white;
    border-radius: 50px;
    box-shadow: 0 5px 20px 0 rgba(0, 0, 0, 0.24), 0 5px 20px 0 rgba(0, 0, 0, 0.5);
}

.newPage .product-shimmer-container .flexDisplay {
    display: flex;
    justify-content: center;
    align-items: center;
}

.newPage .product-shimmer-container .flexDisplay .image-div {
    height: 50px;
    width: 120px;
}

.newPage .product-shimmer-container .flexDisplay .tagLine {
    margin-top: 20px;
    width: 50%;
    height: 20px;
}

.newPage .product-shimmer-container .flexDisplay .button {
    margin-top: 20px;
    width: 70%;
    height: 50px;
}

.newPage .product-shimmer-container .flexDisplay .loginTitle {
    margin-top: 20px;
    width: 70%;
    height: 15px;
}

@media only screen and (max-width: 990px) {
    .fetch-location {
        background-size: cover;
        background-position: bottom;
    }
}

@media only screen and (max-width: 730px) {
    .newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .profile .profileDrop i {
        color: var(--white) !important;
    }

    .newPage /deep/ app-header-dynamic .top-header .header .nav-right .items .profile .dropdown-menu {
        left: -6rem !important;
    }

    .newPage .fetch-location {
        background-size: contain;
        background-position: top;
        background-color: rgba(25, 25, 25, .95);
    }

    .newPage .fetch-location::before {
        background: -moz-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) 32%);
        background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) 32%);
        background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) 32%);
        filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#00ffffff', endColorstr='#ffffff', GradientType=0);
        opacity: 0;
    }

    .newPage .fetch-location .main {
        padding-top: 0px;
        position: absolute;
        bottom: 80px;
        width: 100%;
    }

    .newPage .fetch-location .main .root .pop {
        border-radius: 0px;
        width: 100%;
        min-width: unset;
        box-shadow: none;
        padding: 30px;
        background: transparent;
    }
}

:host /deep/ app-autocomplete app-map-popup app-modal div.model-dialog {
    margin-top: 85px !important;
}


.card-shimmer-container {
    padding: 15px;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
}

.card-shimmer-container:after {
    content: '';
    background: linear-gradient(-45deg, #ddd 0, #f0f0f0 0, #ddd 12, #f0f0f0 4a);
    background-size: 535% 100%;
    -webkit-animation: 2.25s infinite Gradient;
    animation: 2.25s infinite Gradient;
    width: 100%;
    z-index: 9999;
    position: absolute;
    height: 100%;
    background-repeat: no-repeat;
}

.fpo {
    position: relative;
    margin: 25% auto;
